import React from 'react';
import style from './Homework.module.css';

const GalleryView = ({ gv }) => {

  const gvTags = [];
  for (let i = 0; i < gv.length; i += 2) {
    gvTags.push(
      <div className={style.row} key={`row-${i / 2}`}>
        <div className={style.col}>
          <div className={style.gvResult} key={gv[i].galContentId}>
            <div className={style.header}>
              <div>{gv[i].galTitle}</div>
              <div>{gv[i].galPhotographyLocation}</div>
            </div>
            <div className={style.body}><img src={gv[i].galWebImageUrl} alt='imgURL'></img></div>
            <div className={style.footer}>{gv[i].galSearchKeyword}</div>
          </div>
        </div>
        {gv[i + 1] && (
          <div className={style.col}>
            <div className={style.gvResult} key={gv[i + 1].galContentId}>
              <div className={style.header}>
                <div>{gv[i + 1].galTitle}</div>
                <div>{gv[i + 1].galPhotographyLocation}</div>
              </div>
              <div className={style.body}><img src={gv[i + 1].galWebImageUrl} alt='imgURL'></img></div>
              <div className={style.footer}>{gv[i + 1].galSearchKeyword}</div>
            </div>
          </div>
        )}
      </div>
    );
  }

  const gvHeader = gvTags.slice(0, Math.ceil(gvTags.length / 3));
  const gvBody = gvTags.slice(Math.ceil(gvTags.length / 3), Math.ceil(gvTags.length * 2 / 3));
  const gvFooter = gvTags.slice(Math.ceil(gvTags.length * 2 / 3));


  return (
    <div>
    <div>
      {gvHeader}
    </div>
        <div>
        {gvBody}
      </div>

<div>
{gvFooter}
</div>
</div>

  );
};

export default GalleryView;
